package akingspackage;

public class BreakingPoint {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//This code is to implement a workout plan
		//This list workout days and rest days
		//This loops until it get to 4th and 6th day
		for (int  x= 1; x<= 30; x++ ) {
			if(x == 4 || x == 6 ) {
			System.out.println("rest day" +" "+ x);
			
			}
			else {
			System.out.println("work Hard Everyday" + " " + x  );
		}
		
	}
	
}
}
	


