package akingspackage;

public class MathProblem {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 6;
		++x;
		x--;
		System.out.println(x++);
		System.out.println(x);
		System.out.println(++x);
		System.out.println(x--);
		System.out.println(--x);
		int y = 6;
		y = x - 2;
		System.out.println("Y is " + y);
	}

}
 